import React, { useEffect, useState } from 'react';
import { fetchPhotos } from '../services/api';
import PhotoCard from '../components/PhotoCard';
import './PhotoGallery.css';

const PhotoGallery: React.FC = () => {
  const [photos, setPhotos] = useState<any[]>([]);
  const [title, setTitle] = useState<string>('');
  const [albumTitle, setAlbumTitle] = useState<string>('');
  const [email, setEmail] = useState<string>('');
  const [limit, setLimit] = useState<number>(25);
  const [offset, setOffset] = useState<number>(0);
  const [totalItems, setTotalItems] = useState<number>(0);
  const [error, setError] = useState<string | null>(null);

  const buildQuery = () => {
    let query = `?limit=${limit}&offset=${offset}`;
    if (title) query += `&title=${title}`;
    if (albumTitle) query += `&album.title=${albumTitle}`;
    if (email) query += `&album.user.email=${email}`;
    return query;
  };

  useEffect(() => {
    const loadPhotos = async () => {
      try {
        const { data, total } = await fetchPhotos(buildQuery());
        setPhotos(data);
        setTotalItems(total);
        setError(null);
      } catch (error) {
        setError('Failed to fetch photos.');
      }
    };
    loadPhotos();
  }, [title, albumTitle, email, limit, offset]);

  const currentPage = Math.floor(offset / limit) + 1;

  return (
    <div className="photo-gallery">
      <div className="filters">
        <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
        <input type="text" placeholder="Album Title" value={albumTitle} onChange={(e) => setAlbumTitle(e.target.value)} />
        <input type="text" placeholder="User Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <select onChange={(e) => setLimit(Number(e.target.value))} value={limit}>
          <option value={25}>25</option>
          <option value={50}>50</option>
          <option value={100}>100</option>
        </select>
        <input type="number" placeholder="Offset" value={offset} onChange={(e) => setOffset(Number(e.target.value))} />
      </div>
      <div className="pagination">
        <button onClick={() => setOffset(prev => Math.max(prev - limit, 0))}>Previous</button>
        <span>Page {currentPage}</span>
        <button onClick={() => setOffset(prev => prev + limit)}>Next</button>
      </div>
      <div className="pagination-info">
        <span>Showing {photos.length} of {totalItems} items</span>
      </div>
      {error && <div className="error">{error}</div>}
      <div className="photo-list">
        {photos.map(photo => <PhotoCard key={photo.id} photo={photo} />)}
      </div>
    </div>
  );
};

export default PhotoGallery;
