import React, { useEffect, useState } from 'react';
import { fetchPhotos } from '../services/api';
import PhotoCard from '../components/PhotoCard';
import '../styles/PhotoGallery.css';

const PhotoGallery: React.FC = () => {
  const [photos, setPhotos] = useState<any[]>([]);
  const [query, setQuery] = useState<string>('');
  const [limit, setLimit] = useState<number>(25);
  const [offset, setOffset] = useState<number>(0);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadPhotos = async () => {
      try {
        const data = await fetchPhotos(`?limit=${limit}&offset=${offset}${query}`);
        setPhotos(data);
        setError(null);
      } catch (error) {
        setError('Failed to fetch photos.');
      }
    };
    loadPhotos();
  }, [query, limit, offset]);

  return (
    <div className="photo-gallery">
      <div className="filters">
        <input type="text" placeholder="Title" onChange={(e) => setQuery(`&title=${e.target.value}`)} />
        <input type="text" placeholder="Album Title" onChange={(e) => setQuery(`&album.title=${e.target.value}`)} />
        <input type="text" placeholder="User Email" onChange={(e) => setQuery(`&album.user.email=${e.target.value}`)} />
      </div>
      <div className="pagination">
        <button onClick={() => setOffset(prev => Math.max(prev - limit, 0))}>Previous</button>
        <button onClick={() => setOffset(prev => prev + limit)}>Next</button>
      </div>
      {error && <div className="error">{error}</div>}
      <div className="photo-list">
        {photos.map(photo => <PhotoCard key={photo.id} photo={photo} />)}
      </div>
    </div>
  );
};

export default PhotoGallery;
